@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix wfdesc: <http://purl.org/wf4ever/wfdesc#> .
@prefix wfprov: <http://purl.org/wf4ever/wfprov#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/kindgrove/AWSSTACCatalog> ;
            sh:resultMessage "Each Artifact should be linked to a ProcessRun that produced it." ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/kindgrove/#ArtifactProvenanceShape> ;
            sh:value <https://example.org/kindgrove/AWSSTACCatalog> ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/kindgrove/DateRange> ;
            sh:resultMessage "Each Artifact should be linked to a ProcessRun that produced it." ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/kindgrove/#ArtifactProvenanceShape> ;
            sh:value <https://example.org/kindgrove/DateRange> ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/kindgrove/DisplaySummaryReportRun> ;
            sh:resultMessage "Each ProcessRun must be described by exactly one wfdesc:Process." ;
            sh:resultPath wfprov:describedByProcess ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:class wfdesc:Process ;
                    sh:message "Each ProcessRun must be described by exactly one wfdesc:Process." ;
                    sh:minCount 1 ;
                    sh:path wfprov:describedByProcess ] ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/kindgrove/Sentinel2L2A> ;
            sh:resultMessage "Each Artifact should be linked to a ProcessRun that produced it." ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/kindgrove/#ArtifactProvenanceShape> ;
            sh:value <https://example.org/kindgrove/Sentinel2L2A> ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/kindgrove/StudyArea> ;
            sh:resultMessage "Each Artifact should be linked to a ProcessRun that produced it." ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:OrConstraintComponent ;
            sh:sourceShape <https://www.ogc.org/rules/kindgrove/#ArtifactProvenanceShape> ;
            sh:value <https://example.org/kindgrove/StudyArea> ],
        [ a sh:ValidationResult ;
            sh:focusNode <https://example.org/kindgrove/DisplaySummaryReportRun> ;
            sh:resultMessage "Each ProcessRun must be associated with a WorkflowRun." ;
            sh:resultPath wfprov:wasPartOfWorkflowRun ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraintComponent sh:MinCountConstraintComponent ;
            sh:sourceShape [ sh:class wfprov:WorkflowRun ;
                    sh:message "Each ProcessRun must be associated with a WorkflowRun." ;
                    sh:minCount 1 ;
                    sh:path wfprov:wasPartOfWorkflowRun ] ] .

